<!DOCTYPE html><html><body><?php $number = 1;//loop para recorrer los primeros 100 numerosfor($number = 1; $number < 101; $number++){  //variables para guardar los modulos de la division del numero por 3 y 5  $value1 = $number % 3;  $value2 = $number % 5;  //variable que guarda el valor de comparar los modulos de la division del numero por 3 y 5  $value3 = $value1 && $value2;  //variable que guardará lo que se va a imprimir  $string = "";  //se evalúa que valor calza con la comparación de los numeros  switch($value3)  {    //en caso de que la comparacion sea 1 se debe mostrar el numero ya que no es divisible ni por 3 ni por 5    case "1":    {      $string = "<p>$number</p>";      break;    }    //en caso de ser 0 se debe analizar si es divisible por 3, 5 o ambos    case "0":    {      //se analiza si es divisible por 3      switch($value1)      {        //si es divisible por 3 se debe mostrar "Linio"        case "0":        {          $string = "<p style=\"color:red;\"><i>Linio</i></p>";          break;        }        //        case "1":        case "2":        {          break;        }        //        default:        {          break;        }      }      //se analiza si es divisible por 5      switch($value2)      {        //si es divisible por 5 se debe mostrar "IT"        case "0":        {          $string = "<p style=\"color:green;\"><i>IT</i></p>";          break;        }        //        case "1":        case "2":        case "3":        case "4":        {          break;        }        //        default:        {          break;        }      }      //se analiza si es divisible por 3 y 5 y se modifica el valor por "Linianos"      if(($value1 == "0") && ($value2 == "0"))      {        $string = "<p style=\"color:blue;\"><b>Linianos</b></p>";;      }      break;    }    //no se debiera llegar a este caso, es por esto que se muestra "ERROR!"    default:    {      echo "<p>ERROR!</p>";      break;    }  }  //finalmente se muestra por pantalla  el valor del numero o su reemplazo  echo $string;}?>  </body></html>