<!DOCTYPE html><html><body><?php //loop para recorrer los primeros 100 numerosfor($number = 1; $number < 101; $number++){  //variables para guardar los modulos de la division del numero por 3 y 5  $value1 = $number % 3;  $value2 = $number % 5;  //variable que guarda el valor de comparar los modulos de la division del numero por 3 y 5  $value3 = $value1 && $value2;  //variable que guardará lo que se va a imprimir  $string = "";  //se evalua si no es divisible ni por 3 ni 5  while($value3 == 1)  {	//y en caso de no ser divisible por 3 no por 5 lo que se imprime es el numero    $string = "<p>$number</p>";    break;  }  //si es que es divisibile por 3 y 5  while($value3 != 1)  {	//se evalua si es divisible por 3	while($value1 == 0)	{	  //y en caso de ser divisible por 3 lo que se imprime es "Linio"	  $string = "<p style=\"color:red;\"><i>Linio</i></p>";      break;	}	//se evalua si es divisible por 5	while($value2 == 0)	{	  //y en caso de ser divisible por 5 lo que se imprime es "IT"	  $string = "<p style=\"color:green;\"><i>IT</i></p>";	  break;	}	//se evalua si es divisible por 3 y 5	if(($value1 == "0") && ($value2 == "0"))    {	  //y en caso de ser divisible por 3 y 5 lo que se imprime es "Linianos"      $string = "<p style=\"color:blue;\"><b>Linianos</b></p>";;    }	break;  }  //finalmente se muestra por pantalla  el valor del numero o su reemplazo  echo $string;}?>  </body></html>